import sys
sys.path.append("C:\\Users\\justa\\Documents\\Coding\\CodingFolder\\Cryptography")

import cryptotools as ct

text = """""".replace(" ", "").replace("\n", "").upper()
text_len = len(text)

key_length = 34

output = ""
columnNumber = text_len//key_length

cosets = ct.splitCosets(key_length, text)

for i in range(0, columnNumber):
    for coset in cosets:
        output += coset[i]
        output += ","
    output += "\n"

print(output)

